import React, { useState } from 'react';

const SearchBar = ({ setSearchTerm }) => {
  const [inputValue, setInputValue] = useState('');

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearchTerm(inputValue);
  };

  return (
    <form onSubmit={handleSubmit} className="flex items-center space-x-4">
      <input 
        type="text" 
        value={inputValue} 
        onChange={handleChange} 
        placeholder="Search products..."
        className="py-2 px-4 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 flex-1"
      />
      <button 
        type="submit" 
        className="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
      >
        Search
      </button>
    </form>
  );
};

export default SearchBar;
