import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductTile from '../components/ProductList/ProductTile/ProductTile';
import FilterBar from '../components/ProductList/FilterBar';
import SearchBar from '../components/ProductList/SearchBar';
import Layout from '../components/Layout'; // Import the Layout component

const ProductListPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <Layout> {/* Wrap the content with the Layout component */}
      <div className="home-page">
        <SearchBar setSearchTerm={setSearchTerm} />
        <FilterBar />
        <div className="product-list">
          {products
            .filter(product =>
              product.name.toLowerCase().includes(searchTerm.toLowerCase())
            )
            .map(product => (
              <ProductTile key={product._id} product={product} />
            ))}
        </div>
      </div>
    </Layout>
  );
};

export default ProductListPage;
