import React, { useContext } from 'react';
import { useParams, Redirect } from 'react-router-dom'; // Import Redirect
import { ProductContext } from '../../context/ProductContext';

const ProductPage = () => {
  const { id } = useParams();
  const { selectedProductId } = useContext(ProductContext);

  if (id !== selectedProductId) {
    // Handle case where user directly navigates to the page without selecting a product
    // Redirect or display an error message
    return <Redirect to="/" />; // Use Redirect component
  }

  // Fetch product details based on ID

  return (
    <div>
      {/* Render product details */}
    </div>
  );
};

export default ProductPage;
